create table databaseschema
(
    fieldid          smallint    not null
        constraint databaseschema_pkey
            primary key,
    schemacategoryid smallint    not null
        constraint databaseschema_schemacategoryid_fkey
            references schemacategory,
    schematableid    smallint    not null
        constraint databaseschema_schematableid_fkey
            references schematable,
    field            varchar(50) not null,
    datatypeid       smallint    not null
        constraint databaseschema_datatypeid_fkey
            references datatype,
    fieldlength      varchar(10),
    isunique         boolean     not null,
    iskey            boolean     not null,
    required         boolean     not null,
    linktableid      smallint
        constraint databaseschema_linktableid_fkey
            references schematable,
    fielddescription varchar(255)
);

alter table databaseschema
    owner to postgres;

